@bot.message_handler(commands=['gpt'])
def gpt1(message):
    chatID=message.from_user.id
    bot.send_message(chatID,'введите промт')
    bot.register_next_step_handler(message, gpt2)
def gpt2(message):
    bot.send_message(message.from_user.id, gpt(message.text))
def gpt(query):
    client = Client()
    try:
        response = client.chat.completions.create(
            model = 'gpt-3.5-turbo',

            messages=[{'role':'user','content': query}]
        )
        return response.choices[0].message.content
    except:
        bot.send_message(query.chat.id, 'не понял')




@bot.message_handler(commands=['weather'])
def weather(message):
    chatID = message.from_user.id
    markup = ReplyKeyboardMarkup(resize_keyboard=True)
    key = KeyboardButton('отправить геопозицию', request_location=True)
    markup.add(key)
    bot_message = bot.send_message(chatID, 'пожалуйста отправьте геопозицию', reply_markup=markup)
    session[chatID]['bot_message'] = bot_message
    bot.register_next_step_handler(message, weather1)
def weather1(message):
    chatID = message.from_user.id
    bot_message = session[chatID]['bot_message']
    bot.delete_message(bot_message.id)
    lat = message.location.latitude
    lon = message.location.longitude
    current_weather = get_temp(lat, lon)
    bot.send_message(chatID, f'Текущая темперптура:{current_weather}')

def get_temp(lat, lon):
    url = f'https://api.open-meteo.com/v1/forecast?latitude={lat}.52&longitude={lon}.41&current=temperature_2m,cloud_cover,wind_speed_10m&hourly=temperature_2m'
    response = requests.get(url)

    if response.status_code == 200:
        data = response.json()
        current_temp = str(data['current']['temperature_2m'])
        return current_temp
    else:
        print(f'ошибка: {response.status_code}')
        return 'не удалось определить погоду'

